let fs = require("fs")
const { execSync } = require("child_process");

async function createMVCAuth(CURR_DIR, projectName) {
  return new Promise((resolve, reject) => {
    try {
      let projPath = `${CURR_DIR}/${projectName}`;
      execSync(`npx create-project ${projectName} codeworks-templates/mvc-auth#main`)
      process.chdir(projPath);
      execSync(`npx create-project ${projectName} codeworks-templates/template-tools#main`)
      fs.cpSync(`${projectName}/`, './', { recursive: true })
      fs.rmSync(projectName, { recursive: true, force: true })
      resolve(`[#] Open Project\n[#] cd into ${projectName}\n[#] type 'code .`)
    } catch (e) {
      reject(e.message)
    }
  })
}

exports.createMVCAuth = createMVCAuth
